INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364143, 'Fostemsavir', 'Ezetimibe', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). Temsavir, the active moiety of fostemsavir, is an inhibitor of OATP 1B1 and 1B3. Additionally, temsavir and two metabolites are inhibitors of BCRP.', 'DDInter', 1767317475, 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostemsavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364144, 'Gemfibrozil', 'Ezetimibe', 'Moderate', 'The safety and efficacy of ezetimibe coadministered with fibrates other than fenofibrate have not been studied. Fibrates can increase cholesterol excretion into the bile and cause cholelithiasis.', 'DDInter', 1767317475, 'Due to the lack of safety and efficacy data, coadministration of ezetimibe with fibrates other than fenofibrate is not recommended. If cholelithiasis is suspected in a patient receiving such a combination, gallbladder studies are indicated and alternative lipid-lowering therapy should be considered.', 'Others', 'Due to the lack of safety and efficacy data, coadministration of ezetimibe with fibrates other than fenofibrate is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364145, 'Ezetimibe', 'Lomitapide', 'Minor', 'Coadministration with lomitapide has not been shown to significantly affect the pharmacokinetics of ezetimibe. No dosage adjustment is required for ezetimibe when coadministered with lomitapide.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364146, 'Ezetimibe', 'Lovastatin', 'Moderate', 'Coadministration with ezetimibe may rarely increase the risk of myopathy and serum transaminase elevations associated with HMG-CoA reductase inhibitors (i.e., statins). The mechanism of interaction is unknown.', 'DDInter', 1767317475, 'Until further information is available, use of a statin in combination with ezetimibe should be approached with caution. Patients should be advised to promptly report to their physician any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. The drugs should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. In addition, liver function tests should be performed at initiation of therapy and according to the recommendations of the HMG-CoA reductase inhibitor.', 'Others', 'Until further information is available, use of a statin in combination with ezetimibe should be approached with caution.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364147, 'Ezetimibe', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', 'DDInter', 1767317475, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364148, 'Ezetimibe', 'Pexidartinib', 'Moderate', 'Coadministration with pexidartinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of multidrug and toxin extrusion protein 1 (MATE1) and 2K (MATE2K) and organic anion transporting polypeptide (OATP) 1B1, 1B3, and 2B1. The proposed mechanism, based on in vitro data, is decreased clearance due to pexidartinib-mediated inhibition of these transport proteins.', 'DDInter', 1767317475, 'Caution is advised if pexidartinib is used concomitantly with drugs that are substrates of MATE1, MATE2K, OATP1B1, OATP1B3, and OATP2B1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if pexidartinib is used concomitantly with drugs that are substrates of MATE1, MATE2K, OATP1B1, OATP1B3, and OATP2B1, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
