INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67192, 'Aluminum hydroxide', 'Potassium citrate', 'Major', 'The concomitant administration of aluminum-containing products (e.g., antacids and phosphate binders) and citrates may significantly increase serum aluminum concentrations, resulting in toxicity. Citrates enhance the gastrointestinal absorption of aluminum by an unknown mechanism, which may involve the formation of a soluble aluminum-citrate complex. Patients with end-stage renal disease are particularly at risk of developing hyperaluminemia and encephalopathy. Fatalities have been reported.', NULL, 'The concomitant use of aluminum- and citrate-containing products should be avoided in renally impaired patients.', NULL, 'The concomitant use of aluminum- and citrate-containing products should be avoided in renally impaired patients. Some experts also recommend that healthy patients should separate doses of aluminum-containing antacids and citrates by 2 to 3 hours.', 'Absorption', 'Magnesium oxide, Magaldrate, Calcium carbonate, Magnesium hydroxide, Magnesium carbonate', 'Potassium chloride, Potassium bicarbonate, Potassium gluconate', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67279/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Davies NT "Anti-nutrient factors affecting mineral utilization." Proc Nutr Soc 38 (1979): 121-8[5] Agencia Espaola de Medicamentos y Productos Sani