INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67237, 'Indomethacin', 'Potassium citrate', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) with potassium salts may increase the risk of hyperkalemia. NSAIDs may produce potassium retention by reducing renal synthesis of prostaglandin E and impairing the renin-angiotensin system.', NULL, 'Closely monitor potassium levels in patients receiving both potassium salts and NSAID therapy, especially those with renal impairment, diabetes, older age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium (e.', NULL, 'Closely monitor potassium levels in patients receiving both potassium salts and NSAID therapy, especially those with renal impairment, diabetes, older age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium (e.g., beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim). Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Rabeprazole, Misoprostol, Glucosamine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67324/', '[1] "Product Information. Potassium Chloride ER (potassium chloride)." Zydus Pharmaceuticals (USA) Inc, Princeton, NJ.[2] Cerner Multum, Inc. "Australian Produc