INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67309, 'Sulindac', 'Potassium citrate', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) with potassium salts may increase the risk of hyperkalemia. NSAIDs may produce potassium retention by reducing renal synthesis of prostaglandin E and impairing the renin-angiotensin system.', NULL, 'Closely monitor potassium levels in patients receiving both potassium salts and NSAID therapy, especially those with renal impairment, diabetes, older age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium (e.', NULL, 'Closely monitor potassium levels in patients receiving both potassium salts and NSAID therapy, especially those with renal impairment, diabetes, older age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium (e.g., beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim). Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67396/', '[1] "Product Information. Potassium Chloride ER (potassium chloride)." Zydus Pharmaceuticals (USA) Inc, Princeton, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. K-Phos Original (potassium acid phosphate)." Beach Pharmaceuticals, Tampa, FL.[4] "Product Information. Phospho-Trin 250 Neutral (potassium phosphate-sodium phosphate)." Patrin Pharma, Skokie, IL.[5] "Product Information. SSKI (potassium iodide)." Upsher-Smith Laboratories Inc, Minneapolis, MN.[6] "Product Information. Potassium Chloride (potassium chloride)." Pharmaceutical Assoc Inc Div Beach Products, Greenville, SC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67310, 'Telmisartan', 'Potassium citrate', 'Major', 'Concomitant use of angiotensin II receptor blockers (ARBs) and potassium salts may increase the risk of hyperkalemia. Inhibition of angiotensin II results in decreased aldosterone secretion, which in turn causes potassium retention. Risk factors for developing severe or life-threatening hyperkalemia may include renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, and concomitant use of other agents that block the renin-angiotensin-aldosterone system or otherwise increase serum potassium levels.', NULL, 'Caution is advised if angiotensin II receptor blockers must be used concurrently with potassium salts, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim.', NULL, 'Caution is advised if angiotensin II receptor blockers must be used concurrently with potassium salts, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim. The combination should generally be avoided in these patients unless absolutely necessary and the benefits outweigh the potential risks. Serum potassium and renal function should be checked prior to initiating therapy and regularly thereafter. Patients should be given counseling on the appropriate levels of potassium and fluid intake, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Amlodipine, Sacubitril, Hydrochlorothiazide, Nebivolol', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67397/', '[1] Walmsley RN, White GH, Cain M, McCarthy PJ, Booth J "Hyperkalemia in the elderly." Clin Chem 30 (1984): 1409-12[2] Jarman PR, Mather HM "Diabetes may be independent risk factor for hyperkalaemia." BMJ 327 (2003): 812[3] Obialo CI, Ofili EO, Mirza T "Hyperkalemia in congestive heart failure patients aged 63 to 85 years with subclinical renal disease." Am J Cardiol 90 (2002): 663-5[4] Jarman PR, Kehely AM, Mather HM "Hyperkalaemia in diabetes: prevalence and associations." Postgrad Med J 71 (1995): 551-2[5] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[6] Large DM, Carr PH, Laing I, Davies M "Hyperkalaemia in diabetes mellitus--potential hazards of coexisting hyporeninaemic hypoaldosteronism." Postgrad Med J 60 (1984): 370-3[7] Perazella MA "Drug-induced hyperkalemia: old culprits and new offenders." Am J Med 109 (2000): 307-14[8] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Perazella MA, Mahnensmith RL "Hyperkalemia in the elderly: drugs exacerbate impaired potassium homeostasis." J Gen Intern Med 12 (1997): 646-56[10] Lawson DH "Adverse reactions to potassium chloride." Q J Med 43 (1974): 433-40[11] Lawson DH, O''Connor PC, Jick H "Drug attributed alterations in potassium handling in congestive cardiac failure." Eur J Clin Pharmacol 23 (1982): 21-5[12] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[13] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 