INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67624, 'Clozapine', 'Clarithromycin', 'Moderate', 'The concomitant administration of some macrolides may result in decreased clearance of clozapine and increased risk of toxicity. The mechanism of this interaction is suspected to be inhibition of the CYP450 hepatic metabolism of clozapine.', NULL, 'While clinical data are limited, clinical and laboratory monitoring may be appropriate whenever a macrolide is added to or withdrawn from therapy.', NULL, 'While clinical data are limited, clinical and laboratory monitoring may be appropriate whenever a macrolide is added to or withdrawn from therapy. Alternatively, azithromycin and dirithromycin may be considered, as they do not inhibit CYP450 and are not expected to affect clozapine metabolism. Patients should be advised to report symptoms such as somnolence, disorientation, slurred speech, seizures, uncontrolled movements, dizziness, irregular heartbeat, palpitations, fever, or hypersalivation to their physician.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67711/', '[1] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Funderburg LG, Vertrees JE, True JE, Miller AL "Seizure following addition of erythromycin to clozapine treatment." Am J Psychiatry 151 (1994): 1840-1[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67625, 'Cobicistat', 'Clarithromycin', 'Moderate', 'Coadministration of a macrolide or ketolide antibiotic (clarithromycin, erythromycin, telithromycin) with cobicistat is expected to increase the plasma concentrations of both drugs. The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme. No pharmacokinetic data are currently available. Clinically, high plasma levels of macrolide or ketolide antibiotics may increase the risk of QT interval prolongation and torsade de pointes arrhythmia.', NULL, 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with cobicistat.', NULL, 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with cobicistat. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope. Some authorities recommend avoiding the concomitant use of fixed combination antiretroviral products containing cobicistat with these antibiotics. For clarithromycin, a dosage reduction by 50% has been recommended in patients with CrCl between 30 and 60 mL/min. However, in patients receiving atazanavir or ritonavir concomitantly with clarithromycin, it has been suggested that the dose of clarithromycin be reduced by 75% in patients with a CrCl of less than 30 mL/min. A similar dosage reduction may be appropriate when clarithromycin is used with cobicistat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67712/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[3] "Product Information. Biaxin (clarithromycin)." Abbott Pharmaceutical, Abbott Park, IL.[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67626, 'Cobimetinib', 'Clarithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme. The risk and/or severity of adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities, and rhabdomyolysis may be increased.', NULL, 'Concomitant use of cobimetinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of cobimetinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of cobimetinib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67713/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
