INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67630, 'Copanlisib', 'Clarithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'The manufacturer recommends reducing the copanlisib dose to 45 mg when concomitant use with potent CYP450 3A4 inhibitors cannot be avoided.', NULL, 'The manufacturer recommends reducing the copanlisib dose to 45 mg when concomitant use with potent CYP450 3A4 inhibitors cannot be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67717/', '[1] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365205);
