INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67636, 'Darifenacin', 'Clarithromycin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of darifenacin, which is partially metabolized by the isoenzyme. Darifenacin is also metabolized by CYP450 2D6, but 3A4 is the major pathway of metabolism in so-called poor metabolizers of 2D6 (approximately 7% of Caucasians and 2% of Asians and those of African descent).', NULL, 'The dosage of darifenacin should not exceed 7.', NULL, 'The dosage of darifenacin should not exceed 7.5 mg/day when used with CYP450 3A4 inhibitors such as ketolide and certain macrolide antibiotics.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67723/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67637, 'Darolutamide', 'Clarithromycin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter.', NULL, 'Caution is advised when darolutamide is used with dual P-gp and potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when darolutamide is used with dual P-gp and potent CYP450 3A4 inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67724/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67638, 'Darunavir', 'Clarithromycin', 'Moderate', 'Coadministration with darunavir/ritonavir may increase the plasma concentrations of clarithromycin. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of clarithromycin.Clarithromycin had no significant effect on the pharmacokinetics of darunavir.', NULL, 'No dosage adjustment of clarithromycin or darunavir is required during coadministration in patients with normal renal function.', NULL, 'No dosage adjustment of clarithromycin or darunavir is required during coadministration in patients with normal renal function. However, clarithromycin dosage should be reduced by 50% in patients with moderate renal impairment (CrCl 30 to 60 mL/min) and 75% in patients with more severe renal impairment (CrCl below 30 mL/min).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67725/', '[1] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[3] Cheng KL, Nafziger AN, Peloquin CA, Amsden GW "Effect of grapefruit juice on clarithromycin pharmacokinetics." Antimicrob Agents Chemother 42 (1998): 927-9', 1767365205);
