INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67650, 'Dexlansoprazole', 'Clarithromycin', 'Moderate', 'Coadministration with clarithromycin may increase the plasma concentrations of lansoprazole. The proposed mechanism is clarithromycin inhibition of intestinal (first-pass) and hepatic metabolism of lansoprazole via CYP450 3A4. Although lansoprazole is primarily metabolized by CYP450 2C19 in the liver, 3A4-mediated metabolism is the predominant pathway in individuals who are 2C19-deficient (approximately 3% to 5% of the Caucasian and 17% to 20% of the Asian population). Additionally, inhibition of P-glycoprotein intestinal efflux transporter by clarithromycin may also contribute to the interaction, resulting in increased bioavailability of lansoprazole.', NULL, 'Although lansoprazole is generally well tolerated, caution may be advised during coadministration with clarithromycin, particularly if higher dosages of one or both drugs are used.', NULL, 'Although lansoprazole is generally well tolerated, caution may be advised during coadministration with clarithromycin, particularly if higher dosages of one or both drugs are used. Dosage adjustment may be necessary in patients who experience excessive adverse effects of lansoprazole.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67737/', '[1] Miura M, Tada H, Yasui-Furukori N, et al. "Effect of clarithromycin on the enantioselective disposition of lansoprazole in relation to CYP2C19 genotypes." Chirality 17 (2005): 338-344[2] Saito M, Yasui-Furukori N, Uno T, et al. "Effects of clarithromycin on lansoprazole pharmacokinetics between CYP2C19 genotypes." Br J Clin Pharmacol 59 (2005):