INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67660, 'Clarithromycin', 'Valproic acid', 'Moderate', 'A single case has been reported in which a patient developed seizures associated with elevated blood valproate levels after erythromycin was added to a stable valproate regimen. The seizures and elevated valproate levels resolved after both drugs were withheld. The patient subsequently tolerated her usual valproate dosage. The mechanism is suspected to be inhibition of the hepatic metabolism of valproate.', NULL, 'While more data are needed, clinicians are encouraged to be aware that this interaction is possible when macrolide antimicrobial agents and valproate are coadministered.', NULL, 'While more data are needed, clinicians are encouraged to be aware that this interaction is possible when macrolide antimicrobial agents and valproate are coadministered. Patients should be advised to notify their physicians if they experience weakness, fatigue, confusion, ataxia, increased seizures, nausea, or vomiting.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67747/', '[1] Redington K, Wells C, Petito F "Erythromycin and valproate interaction." Ann Intern Med 116 (1992): 877-8[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67661, 'Docetaxel', 'Clarithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 or dual CYP450 3A4 and P-glycoprotein (P-gp) inhibitors may significantly increase the plasma concentrations of docetaxel, which is a substrate of both CYP450 3A4 and P-gp.', NULL, 'Concomitant use of docetaxel with CYP450 3A4 inhibitors, particularly potent inhibitors or dual CYP450 3A4 and P-gp inhibitors such as itraconazole, ketoconazole, posaconazole, conivaptan, ceritinib, idelalisib, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics, should generally be avoided.', NULL, 'Concomitant use of docetaxel with CYP450 3A4 inhibitors, particularly potent inhibitors or dual CYP450 3A4 and P-gp inhibitors such as itraconazole, ketoconazole, posaconazole, conivaptan, ceritinib, idelalisib, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics, should generally be avoided. Some authorities recommend avoiding concomitant use of docetaxel during and for 2 weeks after treatment with itraconazole. If concomitant use is required, a reduced dosage of docetaxel should be considered. Based on extrapolation from the pharmacokinetic study in 7 cancer patients, a 50% reduction in the docetaxel dose may be reasonable. Patients should be closely monitored for the development of docetaxel toxicity such as myelosuppression, stomatitis, neurotoxicity (e.g., paraesthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact