INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67709, 'Etravirine', 'Clarithromycin', 'Moderate', 'Coadministration with etravirine may decrease the plasma concentrations of clarithromycin and increase concentrations of the active metabolite, 14-hydroxy-clarithromycin. The mechanism is etravirine induction of CYP450 3A4, the isoenzyme that metabolizes clarithromycin to 14-hydroxy-clarithromycin.', NULL, 'Because 14-hydroxy-clarithromycin has demonstrated reduced activity relative to the parent drug against Mycobacterium avium complex (MAC), overall activity against this pathogen may be altered during coadministration of clarithromycin with etravirine.', NULL, 'Because 14-hydroxy-clarithromycin has demonstrated reduced activity relative to the parent drug against Mycobacterium avium complex (MAC), overall activity against this pathogen may be altered during coadministration of clarithromycin with etravirine. Therefore, alternatives to clarithromycin (e.g., azithromycin) should be considered for the treatment of MAC in patients receiving etravirine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67796/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[3] Cheng KL, Nafziger AN, Peloquin CA, Amsden GW "Effect of grapefruit juice on clarithromycin pharmacokinetics." Antimicrob Agents Chemother 42 (1998): 927-9', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67710, 'Everolimus', 'Clarithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia may be increased.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided. Some authorities recommend avoiding concomitant use of everolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67797/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Homma S, Takahashi KI, Nihei S, Kato F, Sugihara S, Nunoda S "The successful management of respiratory complications with long-term, low-dose macrolide administration in pediatric heart transplant recipients." Int Heart J (2014):[5] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67711, 'Exenatide', 'Clarithromycin', 'Moderate', 'Concomitant use of insulin and other antidiabetic agents with clarithromycin may cause significant hypoglycemia. There have been rare reports of hypoglycemia in patients treated with clarithromycin, some of whom were taking oral hypoglycemic agents or insulin. The exact mechanism of interaction has not been established. With certain hypoglycemic drugs such as nateglinide, pioglitazone, repaglinide and rosiglitazone, inhibition of the CYP450 3A4 isoenzyme by clarithromycin may be involved.', NULL, 'Caution is advised if clarithromycin is prescribed to patients receiving antidiabetic agents.', NULL, 'Caution is advised if clarithromycin is prescribed to patients receiving antidiabetic agents. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67798/', '[1] Otsuka SH "Severe hypoglycemia from helicobacter pylori triple-drug therapy and insulin detemir drug interaction." Pharmacotherapy 33 (2013): e45-9[2] Niemi M, Neuvonen PJ, Kivisto KT "The cytochrome P4503A4 inhibitor clarithromycin increases the plasma concentrations and effects of repaglinide." Clin Pharmacol Ther 70 (2001): 58-65[3] Schelleman H, Bilker WB, Brensinger CM, Wan F, Hennessy S "Anti-infectives and the risk of severe hypoglycemia in users of glipizide or glyburide." Clin Pharmacol Ther 88 (20