INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67755, 'Indinavir', 'Clarithromycin', 'Minor', 'Coadministration of indinavir (800 mg every eight hours) and clarithromycin (500 mg every 12 hours) for one week increased the area under the plasma concentration-time curve (AUC) of indinavir by 29%. The AUC of clarithromycin increased 53%. The mechanism of this interaction is related to inhibition of CYP450 3A4 hepatic metabolism. The patient should be monitored for drug-related toxicities and be advised to report symptoms such as nausea, vomiting, diarrhea, flank or back pain, or hematuria. Dose reductions may be necessary if evidence of an interaction is observed. Azithromycin and dirithromycin are not expected to interact and may be considered as alternatives.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67842/', '[1] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[2] Boruchoff SE, Sturgill MG, Grasing KW, et al. "The steady-state disposition of indinavir is not altered by the concomitant administration of clarithromycin." Clin Pharmacol Ther 67 (2000): 351-9[3] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8[4] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[5] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[6] Cheng KL, Nafziger AN, Pelo