INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67788, 'Clarithromycin', 'Lactitol', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67875/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67789, 'Clarithromycin', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67876/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767365205);
