INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67844, 'Clarithromycin', 'Midazolam', 'Moderate', 'The macrolide antibiotics clarithromycin and erythromycin may increase and prolong the CNS effects of certain benzodiazepines. The mechanism is inhibition of CYP450 3A4 hepatic oxidation of the benzodiazepines. The effect appears most pronounced with oral midazolam and triazolam.', NULL, 'The use of oral midazolam or triazolam in combination with clarithromycin or erythromycin should generally be avoided.', NULL, 'The use of oral midazolam or triazolam in combination with clarithromycin or erythromycin should generally be avoided. Some authorities consider the use of oral midazolam with clarithromycin contraindicated. Patients receiving triazolam or IV midazolam in combination with erythromycin or clarithromycin should be monitored for excessive or prolonged sedation. Dose adjustment of the benzodiazepine should be considered. Some authorities suggest a dose reduction of midazolam during long-term IV infusion of up to 50% of the initial dose followed by careful titration. Non-interacting benzodiazepines or antimicrobials may be considered as alternatives.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67931/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Versed (midazolam)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Halcion (triazolam)." Pharmacia and Upjohn, Kalamazoo, MI.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Welling PG, Huang H, Hewitt PF, Lyons LL "Bioavailability of erythromycin stearate: influence of food and fluid volume." J Pharm Sci 67