INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67915, 'Clarithromycin', 'Pimavanserin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Pimavanserin dosage should be reduced to 10 mg once a day when used with potent CYP450 3A4 inhibitors.', NULL, 'Pimavanserin dosage should be reduced to 10 mg once a day when used with potent CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68002/', '[1] "Product Information. Nuplazid (pimavanserin)." Accelis Pharma, East Windsor, NJ.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365205);
