INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67917, 'Clarithromycin', 'Pimozide', 'Major', 'Coadministration with the ketolide, telithromycin, as well as certain macrolide antibiotics may increase the plasma concentrations of pimozide. The mechanism is inhibition of pimozide metabolism via CYP450 3A4. High plasma levels of pimozide have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death. Macrolides that may significantly inhibit CYP450 3A4 include clarithromycin, erythromycin, and troleandomycin.', NULL, 'The use of pimozide with telithromycin or macrolide antibiotics is considered contraindicated by the manufacturers, although azithromycin and dirithromycin are generally believed to have little, if any, effect on CYP450 3A4.', NULL, 'The use of pimozide with telithromycin or macrolide antibiotics is considered contraindicated by the manufacturers, although azithromycin and dirithromycin are generally believed to have little, if any, effect on CYP450 3A4.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68004/', '[1] Krahenbuhl S, Sauter B, Kupferschmidt H, Krause M, Wyss PA, Meier PJ "Case report: reversible QT prolongation with torsades de pointes in a patient with pimozide intoxication." Am J Med Sci 309 (1995): 315-6[2] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European Public Assessment Report Ketek (telithromycin) (Rev. 2) Available from: URL: http:www.emea.eu.int/humandocs/Humans/EPAR/Ketek/Ketek.htm." ([2001]):