INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67924, 'Clarithromycin', 'Pomalidomide', 'Moderate', 'Coadministration with potent inhibitors of CYP450 1A2, CYP450 3A4, and P-glycoprotein may increase the plasma concentrations of pomalidomide, which has been shown to be primarily metabolized by these isoenzymes and also a substrate of the efflux transporter. Pomalidomide exposure is increased when given with a strong CYP450 1A2 inhibitor (e.g., fluvoxamine) in the presence of a strong CYP450 3A4 and P-gp inhibitor (e.g., ketoconazole). Coadministration with ketoconazole alone did not have a clinically significant effect on exposure to pomalidomide.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.g., ciprofloxacin, fluvoxamine, tiabendazole) in the presence of strong CYP450 3A4 and P-gp inhibitors should generally be avoided. If coadministration is considered clinically necessary, the pomalidomide dose should be reduced by 50%. Dose reduction may also be required if pomalidomide is given with a strong inhibitor of CYP450 1A2 in the absence of a coadministered CYP450 3A4 and P-gp inhibitor. Patients should be monitored for occurrence of pomalidomide-related side effects, including nausea, diarrhea, and neutropenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68011/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, 