INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67955, 'Clarithromycin', 'Retapamulin (topical)', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme. However, dosage adjustments are not necessary due to the low systemic exposure to retapamulin following topical application.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68042/', '[1] "Product Information. Altabax (retapamulin topical)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67956, 'Clarithromycin', 'Ribociclib', 'Major', 'Ribociclib can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In general, the risk of an individual agent or a combination of agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia). In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). Coadministration with potent CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of ribociclib, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration of ribociclib with other drugs that can prolong the QT interval and are CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Coadministration of ribociclib with other drugs that can prolong the QT interval and are CYP450 3A4 inhibitors should generally be avoided. Since the magnitude of QT prolongation may increase with increasing plasma concentrations of ribociclib, caution and close clinical monitoring are recommended if concomitant use with these drugs is unavoidable. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, the dose of ribociclib should be reduced to 400 mg once daily. Following discontinuation of the potent CYP450 3A4 inhibitor, the ribociclib dosage should be returned (after at least 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor. In addition, ECGs should be assessed prior to initiation of treatment, during treatment when clinically necessary, and more frequently if QTcF prolongation occurs at any time during treatment. Ribociclib should be permanently discontinued if the QTcF interval prolongation is either greater than 500 msec or there is a greater than 60 msec change from baseline and associated with any of the following: Torsades de Pointes, polymorphic ventricular tachycardia, unexplained syncope, or signs/symptoms of serious arrhythmia.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68043/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67957, 'Clarithromycin', 'Rifabutin', 'Moderate', 'The coadministration of clarithromycin and rifabutin at normally recommended dosages has been reported to have resulted in significantly altered pharmacokinetics for both drugs. This bidirectional interaction is consistent with rifabutin''s cumulative inducing effect over time on the CYP450 enzymatic pathway as well as clarithromycin''s immediate inhibiting effect on the pathway. Other macrolide antibiotics may interact in a similar manner with rifamycins.', NULL, 'Some experts recommend that this combination be avoided since it may result in decreased efficacy of the macrolide and increased rifamycin toxicity (e.', NULL, 'Some experts recommend that this combination be avoided since it may result in decreased efficacy of the macrolide and increased rifamycin toxicity (e.g, neutropenia, uveitis).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68044/', '[1] Wallace RJ, Brown BA, Griffith DE, Girard W, Tanaka K "Reduced serum levels of clarithromycin in patients treated with multidrug regimens including rifampin or rifabutin for Mycobacterium avium -M.intracellulare infection." J Infect Dis 171 (1995): 747-50[2] Griffith DE, Brown BA, Girard WM, Wallace RJ Jr "Adverse events associated with high-dose rifabutin in macrolide-containing regimens for the treatment of Mycobacterium avium complex lung disease." Clin Infect Dis 21 (1995): 594-8[3] Apseloff G, Foulds G, LaBoy-Goral L, Willavize S, Vincent J "Comparison of azithromycin and clarithromycin in their interactions with rifabutin in healthy volunteers." J Clin Pharmacol 38 (1998): 830-5[4] von Rosenstiel NA, Adam D "Macrolide antibacterials. Drug interactions of clinical significance." Drug Saf 13 (1995): 105-22[5] Hafner R, Bethel J, Power M, et al. "Tolerance and pharmacokinetic interactions of rifabutin and clarithromycin in human immunodeficiency virus-infected volunteers." Antimicrob Agents Chemother 42 (1998): 631-9[6] Benson CA, Williams PL, Cohn DL, Becker S, Hojczyk P, Nevin T, Korvick JA, Heifets L, Child CC, Lederman MM, Reichman RC, "Clarithromycin or rifabutin alone or in combination for primary prophylaxis of Mycobacterium avium complex disease in patients with AIDS: A randomized, double-blind, placebo-controlled trial." J Infec Dis 181 (2000): 1289-97[7] Jordan MK, Polis MA, Kelly G, Narang PK, Masur H, Piscitelli SC "Effects of fluconazole and clarithromycin on rifabutin and 25-O-desacetylrifabutin pharmacokinetics." Antimicrob Agents Chemother 44 (2000): 2170-72[8] Benson CA, Williams PL, Currier JS, et al. "A Prospective, Randomized Trial Examining the Efficacy and Safety of Clarithromycin in Combination with Ethambutol, Rifabutin, or Both for the Treatment of Disseminated Mycobacterium avium Complex Disease in Persons with Acquired Immunodeficiency Syndrome" Clin Infect Dis 37 (2003): 1234-43', 1767365205);
