INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67967, 'Clarithromycin', 'Rivaroxaban', 'Moderate', 'Coadministration with weak or moderate dual inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications.', NULL, 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68054/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.[2] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[3] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67968, 'Clarithromycin', 'Rivastigmine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68055/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67969, 'Clarithromycin', 'Romidepsin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of romidepsin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when romidepsin is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when romidepsin is used with potent CYP450 3A4 inhibitors. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68056/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
