INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67987, 'Clarithromycin', 'Siponimod', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of siponimod treatment in patients receiving drugs that prolong the QT interval.', NULL, 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with siponimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68074/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67988, 'Clarithromycin', 'Sirolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', NULL, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68075/', '[1] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[2] Dodds-Ashley E "Management of drug and food interactions with azole antifungal agents in transplant recipients." Pharmacotherapy 30 (2010): 842-54[3] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Claesson K, Brattstrom C, Burke JT "Sirolimus and erythromycin interaction: two cases." Transplant Proc 33 (2001): 2136[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Floren LC, Christians U, Zimmerman JJ, et al "Sirolimus oral bioavailability increases ten-fold with concomitant ketoconazole." Clin Pharmacol Ther 65 (1999): 159[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[8] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[9] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[10] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (67989, 'Clarithromycin', 'Sitagliptin', 'Moderate', 'Concomitant use of insulin and other antidiabetic agents with clarithromycin may cause significant hypoglycemia. There have been rare reports of hypoglycemia in patients treated with clarithromycin, some of whom were taking oral hypoglycemic agents or insulin. The exact mechanism of interaction has not been established. With certain hypoglycemic drugs such as nateglinide, pioglitazone, repaglinide and rosiglitazone, inhibition of the CYP450 3A4 isoenzyme by clarithromycin may be involved.', NULL, 'Caution is advised if clarithromycin is prescribed to patients receiving antidiabetic agents.', NULL, 'Caution is advised if clarithromycin is prescribed to patients receiving antidiabetic agents. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68076/', '[1] Otsuka SH "Severe hypoglycemia from helicobacter pylori triple-drug therapy and insulin detemir drug interaction." Pharmacotherapy 33 (2013): e45-9[2] Niemi M, Neuvonen PJ, Kivisto KT "The cytochrome P4503A4 inhibitor clarithromycin increases the plasma concentrations and effects of repaglinide." Clin Pharmacol Ther 70 (2001): 58-65[3] Schelleman H, Bilker WB, Brensinger CM, Wan F, Hennessy S "Anti-infectives and the risk of severe hypoglycemia in users of glipizide or glyburide." Clin Pharmacol Ther 88 (2010): 214-22[4] Khamaisi M, Leitersdorf E "Severe hypoglycemia from clarithromycin-repaglinide drug interaction." Pharmacotherapy 28 (2008): 682-4[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Leiba A, Leibowitz A, Grossman E "An unusual case of hypoglycemia in a diabetic patient." Ann Emerg Med 44 (2004): 427-8[7] Bussing R, Gende A "Severe hypoglycemia from clarithromycin-sulfonylurea drug interaction." Diabetes Care 25 (2002): 1659-61[8] "Product Information. Biaxin (clarithromycin)." Abbott Pharmaceutical, Abbott Park, IL.[9] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[10] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[11] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[12] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[13] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[14] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[15] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6[16] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[17] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[18] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[19] Cheng KL, Nafziger AN, Peloquin CA, Amsden GW "Effect of g