    ) VALUES (1364194, 'Benazepril', 'Levodopa', 'Moderate', 'The hypotensive effects of levodopa and antihypertensive agents may be additive. Postural hypotension may occur.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Dose adjustments of the antihypertensive agent may be required. Patients should be advised to notify their physician if they experience dizziness or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
