INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136339, 'Anisindione', 'Meloxicam', 'Major', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the hypoprothrombinemic effect and bleeding risk associated with oral anticoagulants.', NULL, 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk.', NULL, 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk. The INR should be checked frequently and oral anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of NSAIDs in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools. Salicylates (except aspirin) appear to have less effect on coagulation and may be preferable in patients treated with oral anticoagulants.', 'Synergism', 'Ropivacaine, Lidocaine, Chloroprocaine, Levobupivacaine, Bupivacaine, Tetracaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136496/', '[1] Loftin JP, Vesell ES "Interaction between sulindac and warfarin: different results in normal subjects and in an unusual patient with a potassium-losing renal tubular defect." J Clin Pharmacol 19 (1979): 733-42[2] Gabb GM "Fatal outcome of interaction between warfarin and a non-steroidal anti-inflammatory drug." Med J Aust 164 (1996): 700-1[3