INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (68807, 'Tapentadol', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Rare cases of generalized tonic-clonic seizures and/or loss of consciousness in association with low serum osmolality and electrolyte abnormalities (e.g., hyponatremia, hypokalemia, hypocalcemia, hypomagnesemia) have been reported with the use of bowel cleansing products in patients with no prior history of seizures. The condition resolved with correction of fluid and electrolyte abnormalities.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68894/', '[1] Salik JM, Kurtin P "Severe hyponatremia after colonoscopy preparation in a patient with the acquired immune deficiency syndrome." Am J Gastroenterol 80 (1985): 177-9[2] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories, Braintree, MA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Fleet Phospho Soda (sodium biphosphate-sodium phosphate)." Fleet, CB, Lynchburg, VA.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories, Braintree, MA.[7] "Product Information. Visicol (sodium biphosphate-sodium phosphate)." Salix Pharmaceuticals, Raleigh, NC.[8] "Product Information. Golytely (polyethylene glycol electrolyte solution)." Braintree, Braintree, MA.[9] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (68808, 'Tedizolid', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate. The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis. The clinical significance of this effect remains unknown.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic. If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68895/', '[1] "Product Information. Clenpiq (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[2] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[3] "MHRA. Medicines & Healthcare products Regulatory Agency (general site Reference) Available from: URL: http://www.mhra.gov.uk/spc-pil/index.htm."[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[6] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[7] "Product Information. Golytely (polyethylene glycol electrolyte solution)." Braintree, Braintree, MA.', 1767365205);
