INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364832, 'Benzhydrocodone', 'Sildenafil', 'Moderate', 'Acute intake of opiates during sildenafil use may result in prolonged erections. The proposed mechanism is an opiate-induced acute increase in cyclic guanosine monophosphate concentrations in peripheral nerve endings, which can be additive with that produced by sildenafil.', 'DDInter', 1767317475, 'Patients using sildenafil should be advised of the potential for prolonged erections during acute intake of opiates, and to contact their physician if problems occur. The interaction may not occur during chronic use of opiates.', 'Synergism', 'Patients using sildenafil should be advised of the potential for prolonged erections during acute intake of opiates, and to contact their physician if problems occur.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364833, 'Benzhydrocodone', 'Silodosin', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
