INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364857, 'Benzhydrocodone', 'Terbinafine', 'Moderate', 'Coadministration with terbinafine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by terbinafine, which is expected to occur in patients who are CYP450 2D6 extensive metabolizers (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', 'DDInter', 1767317475, 'Caution is advised if terbinafine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever terbinafine is added to or withdrawn from therapy. Due to the long elimination half-life of terbinafine, especially following prolonged use, such interactions may be observed for several months after discontinuation of terbinafine therapy.', 'Metabolism', 'Caution is advised if terbinafine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364858, 'Benzhydrocodone', 'Tetrabenazine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364859, 'Benzhydrocodone', 'Thalidomide', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', 'DDInter', 1767317475, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
