INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364864, 'Benzhydrocodone', 'Ticagrelor', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 1767317475, 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364865, 'Benzhydrocodone', 'Timolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
