INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364867, 'Benzhydrocodone', 'Tocilizumab', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317475, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364868, 'Benzhydrocodone', 'Tofacitinib', 'Moderate', 'Baricitinib and tofacitinib, both Janus kinase (JAK) inhibitors, have been associated with an increased risk of diverticulitis (DV) and gastrointestinal (GI) perforation, particularly in patients with risk factors (e.g., history of diverticulosis or diverticulitis, concomitant use of other agents associated with DV).', 'DDInter', 1767317475, 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids. Patients should be advised to contact their healthcare provider if they experience signs of DV or GI perforation, such as severe abdominal pain, fever, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
