INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364882, 'Trihexyphenidyl', 'Benzhydrocodone', 'Moderate', 'Central anticholinergic agents may have additive central nervous system (CNS) effects with cannabinoids, barbiturates, opiates, and alcohol. These agents individually can cause cognitive and psychomotor impairment, drowsiness, and dizziness, thus concomitant use may result in more potent effects. In addition, the potential for abuse may be increased when central anticholinergic agents are combined with these drugs.', 'DDInter', 1767317475, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness.', 'Synergism', 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364883, 'Alimemazine', 'Benzhydrocodone', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
