INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364957, 'Citalopram', 'Benzphetamine', 'Major', 'Serotonin reuptake inhibitors may potentiate the pharmacologic response to sympathomimetic agents.', 'DDInter', 1767317475, 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Close monitoring for enhanced sympathomimetic effects and possible serotonin syndrome is recommended if these agents must be used together.', 'Synergism', 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364958, 'Clomipramine', 'Benzphetamine', 'Moderate', 'The use of tricyclic antidepressants in combination with amphetamines or sympathomimetic appetite suppressants may produce additive cardiovascular effects, increasing the risk of hypertension, cardiac arrhythmias, tachycardia, and fever. The mechanism involves additive pharmacodynamic effects resulting from increased norepinephrine release by sympathomimetic agents and inhibition of norepinephrine reuptake by tricyclic antidepressants. A pharmacokinetic interaction is also possible between tricyclic antidepressants and amphetamines, since many of these agents are metabolized by CYP450 2D6. Increased plasma levels of one or both drugs may occur during coadministration.', 'DDInter', 1767317475, 'Close monitoring of cardiovascular status is recommended for patients receiving this combination. Patients should be advised to promptly report symptoms such as fever, headache, or fast or irregular heartbeats.', 'Metabolism, Synergism', 'Close monitoring of cardiovascular status is recommended for patients receiving this combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
