INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364992, 'Glycerol phenylbutyrate', 'Benzphetamine', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', 'DDInter', 1767317475, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364993, 'Guanadrel', 'Benzphetamine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', 'DDInter', 1767317475, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
