INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1365047, 'Benzphetamine', 'Midodrine', 'Moderate', 'The pressor effects of midodrine may be potentiated by the concomitant use of other agents that cause vasoconstriction, including sympathomimetics and ergot alkaloids. Midodrine is an alpha-adrenergic agonist and may cause marked elevation of supine arterial blood pressure, or supine hypertension.', 'DDInter', 1767317475, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure. Supine and sitting blood pressures should be closely monitored. Supine hypertension can often be controlled by keeping the patient from being fully supine, for example, sleeping with the head of the bed elevated. Taking the last daily dose of midodrine 3 to 4 hours before bedtime may also help to minimize nighttime supine hypertension. Patients should be advised to contact their physician immediately if they experience symptoms of supine hypertension such as cardiac awareness, pounding in the ears, headache, and blurred vision. Midodrine should be discontinued if supine hypertension persists.', 'Synergism', 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1365048, 'Benzphetamine', 'Miglitol', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1365049, 'Benzphetamine', 'Milnacipran', 'Major', 'Serotonin reuptake inhibitors may potentiate the pharmacologic response to sympathomimetic agents.', 'DDInter', 1767317475, 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Close monitoring for enhanced sympathomimetic effects and possible serotonin syndrome is recommended if these agents must be used together.', 'Synergism', 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', NULL, NULL, 'Major', 'pharmacodynamic');
