INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69220, 'Ethchlorvynol', 'Clevidipine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Suvorexant, Nitrazepam, Thiopental, Eszopiclone, Ramelteon, Scopolamine, Lemborexant, Methohexital, Estazolam, Zopiclone, Tasimelteon, More', 'Felodipine, Amlodipine, Nisoldipine, Nicardipine, Isradipine, Levamlodipine, Nifedipine, Celecoxib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69307/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69221, 'Fenoldopam', 'Clevidipine', 'Moderate', 'The use of fenoldopam with other hypotensive agents has not been studied. However, these combinations would theoretically increase the risk of severe symptomatic hypotension.', NULL, 'Caution is strongly advised during concurrent use.', NULL, 'Caution is strongly advised during concurrent use. Inpatient monitoring of the heart rate and blood pressure is recommended. Sublingual nitroglycerin has been used safely with fenoldopam.', 'Synergism', 'Dobutamine, Norepinephrine, Midodrine, Amrinone, Droxidopa, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Isoprenaline, More', 'Mibefradil, Celecoxib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69308/', '[1] "Product Information. Corlopam (fenoldopam)." Neurex, Menlo Park, CA.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69222, 'Fingolimod', 'Clevidipine', 'Moderate', 'The risk of bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or AV conduction such as alectinib, atazanavir, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction. Close monitoring is recommended during initiation of fingolimod treatment in these patients. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', 'Celecoxib', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69309/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[2] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69223, 'Fludrocortisone', 'Clevidipine', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Fluocinolone acetonide, Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac', 'Nimodipine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69310/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
