INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69615, 'Clobazam', 'Nortriptyline', 'Moderate', 'Coadministration with clobazam may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by clobazam.', NULL, 'Caution is advised if clobazam must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if clobazam must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever clobazam is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69702/', '[1] Walzer M, Bekersky I, Blum RA, Tolbert D "Pharmacokinetic drug interactions between clobazam and drugs metabolized by cytochrome P450 isoenzymes." Pharmacotherapy 32 (2012): 340-53[2] "Product Information. Onfi (clobazam)." Lundbeck Inc, Deerfield, IL.[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69616, 'Clobazam', 'Olanzapine', 'Moderate', 'The safety and efficacy of intramuscular olanzapine administered in combination with benzodiazepines have not been established. The cause has not been determined but in many of the deaths, patients were treated with multiple concomitant drugs including IM benzodiazepines and other IM antipsychotics that are known to have the potential to induce hypotension, bradycardia, and respiratory or CNS depression. CNS- and/or cardiorespiratory-depressant effects may be increased during concomitant use of olanzapine and benzodiazepines, especially in elderly or debilitated patients.', NULL, 'Caution is necessary when olanzapine is used in combination with benzodiazepines.', NULL, 'Caution is necessary when olanzapine is used in combination with benzodiazepines. Ambulatory patients should be made aware of the possibility of additive CNS effects and counseled to avoid activities requiring mental alertness until they know how these agents affect them. They should also be advised to avoid rising abruptly from a sitting or recumbent position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting. Concomitant administration of IM olanzapine and parenteral benzodiazepine has not been studied and is therefore not recommended. Patients given this combination when necessary should be closely monitored for excessive sedation and cardiorespiratory depression.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69703/', '[1] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapo