INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139168, 'Galantamine', 'Metoprolol', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Ginkgo biloba, Aducanumab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139329/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139169, 'Garlic', 'Metoprolol', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', NULL, '-', NULL, '-', 'Synergism', 'Ivabradine', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139330/', '[1] Tattelman E "Health effects of garlic." Am Fam Physician 72 (2005): 103-6[2] Richard CL, Jurgens TM "Effects of natural health products on blood pressure." Ann Pharmacother 39 (2005): 712-20', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139170, 'Givosiran', 'Metoprolol', 'Moderate', 'Coadministration with givosiran may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2 and/or CYP450 2D6. The proposed mechanism is decreased clearance due to givosiran-mediated inhibition of CYP450 1A2 and 2D6 isoenzymes.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6. Concomitant use should generally be avoided with CYP450 1A2 or 2D6 substrates that have a narrow therapeutic range where minimal concentration changes may lead to serious or life-threatening toxicities. If concomitant use is unavoidable, the dosage of the CYP450 1A2 or 2D6 substrate should be reduced according to approved product labeling or clinical response and tolerance. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever givosiran is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Felodipine, Amlodipine, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Acetylsalicylic acid, Atenolol, Acebutolol, Labetalol, Nadolol, More', 'Teduglutide, Lonafarnib, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139331/', '[1] "Product Information. Givlaari (givosiran)." Alnylam Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139171, 'Glucagon', 'Metoprolol', 'Minor', 'Beta-blockers may blunt the hyperglycemic reaction to glucagon by interfering with beta-adrenergic-mediated hyperglycemic counter-regulatory mechanisms.', NULL, '-', NULL, '-', 'Antagonism', 'Felodipine, Amlodipine, Nebivolol, Nifedipine, Ivabradine, Acetylsalicylic acid, Nebivolol, Nebivolol', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139332/', '[1] Mills GA, Horn JR "B-blockers and glucose control." Drug Intell Clin Pharm 19 (1985): 246-51[2] Smith RC, Wilkinson J, Hull RL "Glucagon for propranolol overdose." JAMA 254 (1985): 2412[3] Smith RC, Wilkinson DO, Hull RL "Glucagon for propranolol overdose." JAMA 254 (1985): 2412[4] Messerli FH, Kuchel O, Tolis G, Hamet P, Fraysse J, Genest J "Effects of B-adrenergic blockade on plasma cyclic AMP and blood sugar responses to glucagon and isoproterenol in man." Int J Clin Pharmacol 14 (1976): 189-94[5] Pun KK, Yeung CK, Chak W, et al "Effects of selective and non-selective beta-blockers on the alanine and free fatty acid responses to glucagon challenge in hemodialysis patients." Clin Nephrol 26 (1986): 222-6', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139172, 'Glycerol phenylbutyrate', 'Metoprolol', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Felodipine, Amlodipine, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Acetylsalicylic acid, Atenolol, Nadolol, Esmolol, Betaxolol, More', 'Teduglutide, Lonafarnib, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139333/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139173, 'Guanadrel', 'Metoprolol', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Felodipine, Acetylsalicylic acid, Amlodipine, Ivabradine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139334/', '[1] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] Kronig MH, Roose SP, Walsh BT, Woodring S, Glassman AH "Blood pressure effects of phenelzine." J Clin Psychopharmacol 3 (1983): 307-10[4] "Product Information. Nardil (phenelzine)." Parke-Dvis, Morris Plains, NJ.[5] Golwyn DH, Sevlie CP "Monoamine oxidase inhibitor hypertensive crisis headache and orthostatic hypotension." J Clin Psychopharmacol 13 (1993): 77-8[6] "Product Information. Parnate (tranylcypromine)." SmithKline Beecham, Philadelphia, PA.[7] De Vita VT, Hahn MA, Oliverio VT "Monoamine oxidase inhibition by a new carcinostatic agent, n-isopropyl-a-(2-methylhydrazino)-p-toluamide (MIH). (30590)." Proc Soc Exp Biol Med 120 (1965): 561-5[8] Pettinger WA, Soyangco FG, Oates JA "Inhibition of monoamine oxidase in man by furazolidone." Clin Pharmacol Ther 9 (1968): 442-7[9] "Product Information. Hylorel (guanadrel)." Rhone-Poulenc Rorer, Collegeville, PA.[10] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1989): 106-8[11] Goldberg LI "Monoamine oxidase inhibitors: adverse reactions and possible mechanisms." JAMA 190 (1964): 456-62[12] "Product Information. Toprol-XL (metoprolol)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[13] Kraus RP "Pindolol augmentation of tranylcypromine in psychotic depression." J Clin Psychopharmacol 17 (1997): 225-6', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139174, 'Guanfacine', 'Metoprolol', 'Moderate', 'Use of central alpha-2 adrenergic agonists, such as clonidine or guanfacine, with beta-blockers may have synergistic pharmacodynamic effects resulting in AV block, bradycardia, and hypotension. In addition, sinus node dysfunction may also be enhanced. Abrupt withdrawal of an alpha-2 agonist or both the alpha-2 agonist and the beta-blocker may also result in rebound hypertensive effects. The proposed mechanism is related to increased catecholamine release after alpha-2 agonist withdrawal which, when combined with concurrent beta-blockade, results in unopposed vasoconstriction.', NULL, 'Until more information is available, caution and close monitoring of blood pressure are recommended for patients receiving guanfacine with a beta-blocker.', NULL, 'Until more information is available, caution and close monitoring of blood pressure are recommended for patients receiving guanfacine with a beta-blocker. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some patients. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, headaches, or reduced or irregular heart rate.', 'Synergism', 'Acetylsalicylic acid, Ivabradine', 'Deserpidine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139335/', '[1] Bailey RR, Neale TJ "Rapid clonidine withdrawal with blood pressure overshoot exaggerated by beta-blockage." Br Med J 1 (1976): 942-3[2] Jounela AJ, Lilja M "Interactions between beta-blockers and clonidine." Ann Clin Res 16