INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139229, 'Norepinephrine', 'Metoprolol', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', 'Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139390/', '[1] "Product Information. Dobutrex (dobutamine)." Lilly, Eli and Company, Indianapolis, IN.[2] 