INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139240, 'Pasireotide', 'Metoprolol', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', NULL, 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', NULL, 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', 'Acetylsalicylic acid', 'Nafarelin, Elagolix, Cetrorelix, Ganirelix, Relugolix, Gonadorelin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139401/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Signifor (pasireotide)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139241, 'Pentobarbital', 'Metoprolol', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Acetylsalicylic acid, Bisoprolol, Atenolol, Sotalol, Nadolol, Esmolol, Betaxolol, Bisoprolol, Carteolol, Sotalol, Pindolol, More', 'Suvorexant, Thiopental, Eszopiclone, Ramelteon, Lemborexant, Methohexital, Melatonin, Valerian', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139402/', '[1] Mantyla R, Mannisto P, Nykanen S, Koponen A, Lamminsivu U "Pharmacokinetic interactions of timolol with vasodilating drugs, food and phenobarbitone in healthy human volunteers." Eur J Clin Pharmacol 24 (1983): 227-30[2] Seideman P, Borg K, Haglund K, Von Bahr C, "Decreased plasma concentrations and clinical effects of alprenolol during combined treatment with pentobarbitone in hypertension." Br J Clin Pharmacol 23 (1987): 267-71[3] Haglund K, Seideman P, Colliste P, Borg KO, von Bahr C "Influence of pentobarbital on metoprolol plasma levels." Clin Pharmacol Ther 26 (1979): 326-9[4] Sotaniemi EA, Anttila M, Pelkonen RO, Jarvensivu P, Sundquist H "Plasma clearance of propranolol and sotalol and hepatic drug-metabolizing enzyme activity." Clin Pharmacol Ther 26 (1979): 153-61[5] Branch RA, Herman RJ "Enzyme induction and beta-adrenergic receptor blocking drugs." Br J Clin Pharmacol 17 (1984): s77-84[6] Collste P, Seideman P, Borg K, Haglund K, Von Bahr C "Influence of pentobarbital on effect and plasma levels of alprenolol and 4-hydroxy-alprenolol." Clin Pharmacol Ther 25 (1979): 423-7', 1767369485);
