INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139280, 'Scopolamine', 'Metoprolol', 'Moderate', 'Anticholinergic agents frequently cause drowsiness and other central nervous system-depressant effects that may be additive with those induced by beta blockers. In addition, these agents may increase heart rate and theoretically may counteract the bradycardic effects of beta blockers. Pharmacokinetically, anticholinergic agents may delay the gastrointestinal absorption of beta blockers and other drugs that are administered orally. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.g., drowsiness, dizziness, lightheadedness, confusion, blurred vision) if anticholinergic agents are used in combination with beta blockers. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism, Absorption', 'Felodipine, Nifedipine, Ivabradine, Esmolol', 'Suvorexant, Thiopental, Eszopiclone, Ramelteon, Lemborexant, Methohexital, Melatonin, Valerian, Tropicamide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139441/', '[1] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[2] Briant RH, Dorrington RE, Ferry DG, Paxton JW "Bioavailability of metoprolol in young adults and the elderly, with additional studies on the effects of metoclopramide and probanthine." Eur J Clin Pharmacol 25 (1983): 353-6[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Clark JM, Seager SJ "Gastric emptying following premedication with glycopyrrolate or atropine." Br J Anaesth 55 (1983): 1195-9', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139281, 'Secobarbital', 'Metoprolol', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Acetylsalicylic acid, Carteolol', 'Suvorexant, Thiopental, Eszopiclone, Ramelteon, Lemborexant, Methohexital, Melatonin, Valerian', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139442/', '[1] Mantyla R, Mannisto P, Nykanen S, Koponen A, Lamminsivu U "Pharmacokinetic interactions of timolol with vasodilating drugs, food and phenobarbitone in healthy human volunteers." Eur J Clin Pharmacol 24 (1983): 227-30[2] Seideman P, Borg K, Haglund K, Von Bahr C, "Decreased plasma concentrations and clinical effects of alprenolol during combined treatment with pentobarbitone in hypertension." Br J Clin Pharmacol 23 (1987): 267-71[3] Haglund K, Seideman P, Colliste P, Borg KO, von Bahr C "Influence of pentobarbital on metoprolol plasma levels." Clin Pharmacol Ther 26 (1979): 326-9[4] Sotaniem