INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139697, 'Oxtriphylline', 'Mexiletine', 'Major', 'Coadministration with mexiletine may increase the serum concentrations of theophylline. The proposed mechanism is inhibition of the CYP450 1A2-mediated metabolism of theophylline by mexiletine.', NULL, 'Caution is advised when theophylline is used with mexiletine.', NULL, 'Caution is advised when theophylline is used with mexiletine. Close monitoring of serum theophylline levels is recommended, particularly following the addition, discontinuation or change of dosage of mexiletine, and the theophylline dosage adjusted accordingly. Patients should be advised to notify their physician if they experience signs of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, palpitations, and cardiac arrhythmia.', 'Metabolism', 'Quinidine, Lidocaine, Dronedarone, Procainamide, Flecainide, Disopyramide, Dofetilide, Bretylium, Ibutilide', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Zafirlukast, Bromotheophylline', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139858/', '[1] Ueno K, Miyai K, Kato M, et al "Mechanism of interaction between theophylline and mexiletine." DICP 25 (1991): 727-30[2] Katz A, Buskila D, Sukenik S "Oral mexiletine-theophylline interaction." Int J Cardiol 17 (1987): 227-8[3] Stoysich AM, Mohiuddin SM, Destache CJ, et al "Influence of mexiletine on the pharmacokinetics of theophylline in healthy volunteers." J Clin Pharmacol 31 (1991): 354-7[4] Stanley R, Comer T, Taylor JL, Saliba D "Mexiletine-theophylline interaction." Am J Med 86 (1989): 733-4[5] K