INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139698, 'Panobinostat', 'Mexiletine', 'Moderate', 'Coadministration with panobinostat may increase the plasma concentrations of drugs that are substrates of CYP450 2D6. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by panobinostat.', NULL, 'Given the high interpatient variability with respect to magnitude of interaction, concomitant use of panobinostat with sensitive CYP450 2D6 substrates (e.', NULL, 'Given the high interpatient variability with respect to magnitude of interaction, concomitant use of panobinostat with sensitive CYP450 2D6 substrates (e.g., atomoxetine, desipramine, dextromethorphan, metoprolol, nebivolol, perphenazine, tolterodine, venlafaxine) or CYP450 2D6 substrates that have a narrow therapeutic index (e.g., pimozide, thioridazine) should generally be avoided. Caution is advised when panobinostat is used with other drugs that are metabolized by CYP450 2D6.', 'Metabolism', 'Lidocaine, Moricizine, Bretylium, Tocainide', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139859/', '[1] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139699, 'Paroxetine', 'Mexiletine', 'Moderate', 'Coadministration with certain selective serotonin reuptake inhibitors (SSRIs) may increase the plasma concentrations of antiarrhythmic agents that are metabolized by CYP450 2D6 such as encainide, flecainide, mexiletine, and propafenone. The mechanism is decreased drug clearance due to inhibition of the isoenzyme by some SSRIs. The interaction has been studied specifically with fluoxetine and propafenone.', NULL, 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with antiarrhythmic agents that are metabolized by CYP450 2D6.', NULL, 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with antiarrhythmic agents that are metabolized by CYP450 2D6. Lower initial dosages of the antiarrhythmic agent may be appropriate. Patients who are already stabilized on their antiarrhythmic regimen should be monitored for altered effects on myocardial conduction following addition or discontinuation of SSRI therapy (up to 2 weeks for most SSRIs and 5 weeks for fluoxetine due to its long half-life), and the antiarrhythmic dosage adjusted if necessary. Alternatively, an antidepressant that does not interfere with 2D6 metabolism may be considered, such as citalopram, escitalopram, or venlafaxine.', 'Metabolism', 'Tocainide, Procainamide, Disopyramide, Bretylium, Moricizine, Ibutilide', 'Citalopram, Maprotiline, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Isocarboxazid, Amoxapine, Imipramine, Esketamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/intera