INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139701, 'Phenytoin', 'Mexiletine', 'Moderate', 'Hydantoins may induce the hepatic metabolism of mexiletine. Plasma mexiletine levels and mexiletine effectiveness may be decreased.', NULL, 'It is recommended that the antiarrhythmic effectiveness of mexiletine be monitored and that mexiletine dosage be increased as needed.', NULL, 'It is recommended that the antiarrhythmic effectiveness of mexiletine be monitored and that mexiletine dosage be increased as needed. Also, when hydantoin is withdrawn, the clinician should be aware of the risk of mexiletine toxicity.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, Paramethadione, More', 'Propafenone, Tocainide, Procainamide, Flecainide, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139862/', '[1] Begg EJ, Chinwah PM, Webb C, Day RO, Wade DN "Enhanced metabolism of mexiletine after phenytoin administration." Br J Clin Pharmacol 14 (1982): 219-23[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[5] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12[6] "Product Information. Dilantin (phenytoin)."