INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139705, 'Quinidine', 'Mexiletine', 'Moderate', 'Quinidine may reduce the hepatic metabolism of mexiletine in extensive debrisoquine metabolizers. The mechanism is inhibition of CYP450 2D6. Serum mexiletine levels and risk of toxicity may be increased. The pharmacologic properties of these drugs may be additive and even though the concurrent use of quinidine and mexiletine has been beneficial in some patients, the risk of proarrhythmic effects may be increased.', NULL, 'The patient should be monitored for clinical, laboratory, and electrocardiographic signs and symptoms of mexiletine toxicity after quinidine has been added to the regimen.', NULL, 'The patient should be monitored for clinical, laboratory, and electrocardiographic signs and symptoms of mexiletine toxicity after quinidine has been added to the regimen. Patients should be advised to notify their physician if they experience nausea, vomiting, dizziness, drowsiness, blurry vision, increased irregular heartbeat, numbness or tingling, seizures, or syncope.', 'Metabolism, Synergism', 'Quinidine, Tocainide, Moricizine, Bretylium', 'Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139866/', '[1] Duff HJ, Rahmberg M, Sheldon RS "Role of quinidine in the mexiletine-quinidine interaction: Electrophysiologic correlates of enhanced antiarrhythmic efficiency." J Cardiovasc Pharmacol 16 (1990): 685-92[2] Duff HJ, Kolodgie FD, Roden DM, Woosley RL "Electropharmacologic synergism with mexiletine and quinidine." J Cardiovasc Pharmacol 8 (1986): 840-6[3] Ace 