INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139707, 'Ranolazine', 'Mexiletine', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Ranolazine has been shown in vitro to be an inhibitor of CYP450 2D6. However, concomitant use of ranolazine with other drugs that are metabolized by CYP450 2D6 such as tricyclic antidepressants and antipsychotics has not been studied.', NULL, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ranolazine is added to or withdrawn from therapy.', 'Metabolism', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Ivabradine, Adenosine, Regadenoson', 'Lidocaine, Moricizine, Bretylium, Tocainide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139868/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139708, 'Rasagiline', 'Mexiletine', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of rasagiline, which is primarily metabolized by the isoenzyme. Because the MAO-B selectivity of rasagiline may diminish with increasing dosage above the recommended range of 0.5 to 1 mg/day, there may be an increased risk of hypertensive crisis and other adverse reactions associated with nonselective inhibition of MAO if the maximum dosage is used in the presence of ciprofloxacin or other CYP450 1A2 inhibitors.', NULL, 'Rasagiline should be prescribed at a maximum dosage of 0.', NULL, 'Rasagiline should be prescribed at a maximum dosage of 0.5 mg/day during coadministration with moderate or potent CYP450 1A2 inhibitors. Patients should be advised to promptly seek medical attention if they experience potential signs and symptoms of a hypertensive crisis such as severe headache, visual disturbances, confusion, stupor or coma, seizures, chest pain, unexplained nausea or vomiting, and stroke-like symptoms.', 'Metabolism', 'Quinidine, Lidocaine, Dronedarone, Tocainide, Procainamide, Flecainide, Disopyramide, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Apomorphine, Opicapone, Cabergoline, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, Safinamide, Levodopa', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139869/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA, North Wales, PA.[4] McGrath PJ, Stewart JW, Q