INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139734, 'Astemizole', 'Miconazole', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of astemizole. The proposed mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of astemizole. High plasma levels of astemizole have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia and torsade de pointes; cardiac arrest; and sudden death.', NULL, 'The use of astemizole with most azole antifungal agents is considered contraindicated.', NULL, 'The use of astemizole with most azole antifungal agents is considered contraindicated. Some authorities consider concomitant administration of astemizole and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole (AU). Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with azole antifungal agents.', 'Metabolism', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Rifaximin, Paromomycin, Neomycin, Caspofungin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139895/', '[1] Venkatakrishnan K, von Moltke LL, Greenblatt DJ "Effects of the antifungal agents on oxidative drug metabolism: clinical relevance." Clin Pharmacokinet 38 (2000): 111-80[2] "Product Information. Diflucan (fluconazole)." Roerig Division, New York, NY.[3] Smith SJ "Cardiovascular t