INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139790, 'Miconazole', 'Mephenytoin', 'Moderate', 'Miconazole may increase plasma hydantoin concentrations. Phenytoin toxicity has been reported with concurrent parenteral and oral miconazole. The mechanism may be related to inhibition of hepatic CYP450 2C9, the major metabolic pathway for phenytoin.', NULL, 'If miconazole and hydantoins must be used concomitantly, caution is recommended, as is close observation for clinical and laboratory evidence of altered phenytoin effect.', NULL, 'If miconazole and hydantoins must be used concomitantly, caution is recommended, as is close observation for clinical and laboratory evidence of altered phenytoin effect. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139951/', '[1] Rolan PE, Somogyi AA, Drew MJ, et al "Phenytoin intoxication during treatment with parenteral miconazole." Br Med J (Clin Res Ed) 287 (1983): 1760[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral