INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139794, 'Mifepristone', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. The clinical impact of this interaction has not been studied.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, caution is advised if mifepristone is prescribed in combination with CYP450 3A4 inhibitors, particularly when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, caution is advised if mifepristone is prescribed in combination with CYP450 3A4 inhibitors, particularly when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', 'Metabolism', 'Raloxifene, Prasterone, Danazol, Bazedoxifene', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139955/', '[1] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[4] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[5] "Product Information. Mifeprex (m