INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139811, 'Panobinostat', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when panobinostat is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when panobinostat is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, anorexia, peripheral edema, cardiotoxicity, ECG abnormalities, electrolyte disturbances, bleeding complications, hepatotoxicity and myelosuppression, and the dosage of panobinostat adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139972/', '[1] Hamberg P, Woo MM, Chen LC, et al. "Effect of ketoconazole-mediated CYP3A4 inhibition on clinical pharmacokinetics of panobinostat (LBH589), an orally active histone deacetylase inhibitor." Cancer Chemother Pharmacol 68 (2011): 805-13[2] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Inf