INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139813, 'Pazopanib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4. Pharmacologic response to pazopanib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the pazopanib dosage adjusted as necessary. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Rifaximin, Paromomycin, Neomycin, Caspofungin, More', 'Pralsetinib, Dabrafenib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Vemurafenib, Selumetinib, Vandetanib, Capmatinib, Afatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139974/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139814, 'Pexidartinib', 'Miconazole', 'Major', 'Coadministration of pexidartinib with moderate CYP450 3A4 inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity. The proposed mechanism is inhibition of CYP450 3A4, the primary isoenzyme responsible for the metabolic clearance of pexidartinib.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided. If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations. If concomitant use of a moderate CYP450 3A4 inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the moderate CYP450 3A4 inhibitor, to the dose that was used prior to starting the moderate CYP450 3A4 inhibitor.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Rifaximin, Paromomycin, Neomycin, Micafungin, More', 'Pralsetinib, Dabrafenib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Vemurafenib, Selumetinib, Vandetanib, Capmatinib, Afatinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139975/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139815, 'Phenytoin', 'Miconazole', 'Moderate', 'Miconazole may increase plasma hydantoin concentrations. Phenytoin toxicity has been reported with concurrent parenteral and oral miconazole. The mechanism may be related to inhibition of hepatic CYP450 2C9, the major metabolic pathway for phenytoin.', NULL, 'If miconazole and hydantoins must be used concomitantly, caution is recommended, as is close observation for clinical and laboratory evidence of altered phenytoin effect.', NULL, 'If miconazole and hydantoins must be used concomitantly, caution is recommended, as is close observation for clinical and laboratory evidence of altered phenytoin effect. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Rifamycin, Kanamycin, Polymyxin B, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, Flucytosine, Anidulafungin, Micafungin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139976/', '[1] Rolan PE, Somogyi AA, Drew MJ, et al "Phenytoin intoxication during treatment with parenteral miconazole." Br Med J (Clin Res Ed) 287 (1983): 1760[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral