INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139851, 'Telaprevir', 'Miconazole', 'Moderate', 'Coadministration of an azole antifungal agent and hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) may result in increased plasma concentrations of both drugs. The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme. Clinically, high plasma levels of an azole antifungal agent may increase the risk of QT interval prolongation and torsade de pointes arrhythmia.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors. Dosage adjustments may be required. According to the product information for boceprevir and telaprevir, high dosages of ketoconazole and itraconazole (>200 mg/day) should be avoided.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Rifaximin, Paromomycin, Neomycin, Fluconazole, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140012/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
