INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139855, 'Tipranavir', 'Miconazole', 'Moderate', 'Coadministration with azole antifungal agents may substantially increase the plasma concentrations of tipranavir administered in combination with low-dose ritonavir. The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of both tipranavir and ritonavir.', NULL, 'In patients treated with tipranavir/ritonavir, dosages of fluconazole, itraconazole, and ketoconazole exceeding 200 mg/day are not recommended.', NULL, 'In patients treated with tipranavir/ritonavir, dosages of fluconazole, itraconazole, and ketoconazole exceeding 200 mg/day are not recommended. Due to multiple enzymes involved with voriconazole metabolism, the interaction is difficult to predict and no recommendations are available.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, More', 'Rifamycin, Kanamycin, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, Micafungin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140016/', '[1] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139856, 'Tofacitinib', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140017/', '[1] "Product Information. ORAVIG (miconazole)." Strativa Pharmaceuticals, a Division of Par Pharmaceuticals, Inc., Woodcliff Lake, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139857, 'Tolazamide', 'Miconazole', 'Major', 'Coadministration with inhibitors of CYP450 2C9 including certain azole antifungal agents such as fluconazole, miconazole, and voriconazole may increase the plasma concentrations of sulfonylureas, many of which have been found to be substrates of the isoenzyme.', NULL, 'Caution is advised when sulfonylureas are used concomitantly with azole antifungal agents that inhibit CYP450 2C9.', NULL, 'Caution is advised when sulfonylureas are used concomitantly with azole antifungal agents that inhibit CYP450 2C9. Blood glucose should be closely monitored, and the sulfonylurea dosage adjusted as necessary. Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the azole antifungal agent, and contact their physician.', 'Metabolism', 'Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Ertugliflozin, Semaglutide, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140018/', '[1] Abad S, Moachon L, Blanche P, Bavoux F, Sicard D, Salmon-Ceron D "Possible interaction between glicazide, fluconazole and sulfamethoxazole resulting in severe hypoglycaemia." Br J Clin Pharmacol 52 (2001): 456-7[2