INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139868, 'Upadacitinib', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140029/', '[1] "Product Information. ORAVIG (miconazole)." Strativa Pharmaceuticals, a Division of Par Pharmaceuticals, Inc., Woodcliff Lake, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139869, 'Valdecoxib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of valdecoxib, which is metabolized by these isoenzymes.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast. Dose reductions of the COX-2 inhibitor may be required.', 'Metabolism', 'Rabeprazole, Ketoprofen, Piroxicam, Meclofenamic acid, Sulindac, Etodolac, Ketorolac, Misoprostol, Glucosamine, Oxaprozin, Esomeprazole, More', 'Rifamycin, Amphotericin B, Polymyxin B, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, Caspofungin, Micafungin, Amphotericin B, Posaconazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140030/', '[1] "Product Information. Bextra (valdecoxib)." Pharmacia Corporation, Peapack, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139870, 'Venetoclax', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venetoclax, which is a substrate of both the isoenzyme and efflux transporter. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', NULL, 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4.', NULL, 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4. Close clinical and laboratory monitoring for the development of tumor lysis syndrome and myelosuppression is recommended following the addition of a CYP450 3A4, and the venetoclax dosage adjusted if necessary. The dosage may be readjusted 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Paromomycin, Neomycin, Caspofungin, Micafungin, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140031/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[4] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
