INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139933, 'Fluconazole', 'Midazolam', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of benzodiazepines that are primarily metabolized by the isoenzyme.', NULL, 'Although clotrimazole, fluconazole, miconazole, and voriconazole are weaker inhibitors of CYP450 3A4 than itraconazole and ketoconazole, product labelings for alprazolam and triazolam recommend against use with any azole antifungal agent.', NULL, 'Although clotrimazole, fluconazole, miconazole, and voriconazole are weaker inhibitors of CYP450 3A4 than itraconazole and ketoconazole, product labelings for alprazolam and triazolam recommend against use with any azole antifungal agent. The same precaution probably applies also to oral midazolam and high dosages of intravenous midazolam. Terbinafine may be an appropriate alternative, as it is not an inhibitor of CYP450 3A4 and has been shown to have no effect on the pharmacokinetics of midazolam and triazolam. Alternatively, benzodiazepines that are not metabolized by CYP450 3A4 (e.g., lorazepam, oxazepam, temazepam) may be considered in patients requiring treatment with azole antifungal agents. Limited data suggest that fluconazole given intermittently (e.g., 150 mg once or once a week) may be safely administered in combination with midazolam and possibly other benzodiazepines.', 'Metabolism', 'Tinidazole, Tetracycline, Norfloxacin, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, Flucytosine, Anidulafungin, Micafungin, More', 'Remimazolam, Butabarbital, Secobarbital, Chloral hydrate, Thiopental, Paraldehyde, Amobarbital, Propiomazine, Butalbi